

        var EMAlerts = new function() {
            this.drive     = "SweetAlert";


            this.error = function (options) {
                UIkit.modal.alert(options.title);

            };

            this.success = function (options) {
                swal({
                    type : 'success',
                    title: options.title || 'Éxito',
                    text : options.message || 'Operación exitosa',
                    html : options.html || ''
                }).then(function(){
                    if( options.callback != undefined )
                        options.callback()
                });
            };

            this.info = function (options) {
                swal({
                    title: options.title,
                    text: options.message,
                    type: 'info',
                    showCancelButton: false,
                }).then(function() {
                    if( options.callback ){
                        options.callback();
                    }
                });
            };

            this.confirm = function (options) {

                UIkit.modal.confirm(options.message, function(){ options.callback(); });

            };


            this.confirmCancel = function(options){
                UIkit.modal.confirm(options.message, function(){ options.callback(); });
            };

            this.input = function(options){
                swal({
                  title: options.title || '',
                  text: options.message || '',
                  input: options.typeInput || 'text',
                  showCancelButton: options.btnCancel || false,
                  confirmButtonText:  options.confirmText || 'Aceptar',
                  cancelButtonText:  options.cancelText || 'Cancelar',
                  cancelButtonColor: options.cancelButtonColor || '',
                  showLoaderOnConfirm: options.loader || false,
                  closeOnConfirm: options.closeOnConfirm || false,
                  inputPlaceholder: options.placeholder || '',
                  inputAttributes: options.attributes || {},
                  preConfirm: options.promiseCallback,
                  allowOutsideClick: false
                }).then(
                    options.successCallback,
                    function(dismiss){}
                );
            };

            this.waiting = function(options){
                swal({
                  type  : options.type || 'warning',
                  title : options.title || '',
                  html  : options.html || '',
                  text  : options.message || '',
                  showConfirmButton   : true,
                  showCancelButton    : options.btnCancel || false,
                  confirmButtonText   : options.confirmText || 'Aceptar',
                  cancelButtonText    : options.cancelText || 'Cancelar',
                  showLoaderOnConfirm : options.loader || false,
                  preConfirm: options.promiseCallback,
                  allowOutsideClick: false,
                  allowEscapeKey: false,
                  reverseButtons: options.reverseButtons || false
                }).then(
                    options.successCallback,
                    function(dismiss){}
                );
            };

        };
